package JavaPractice.JavaPractice;

public class AndOrOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char letter = 'a';

		switch (letter) {
			case 'a':
				System.out.println("This is a vowel");
				break;
			case 'e':
				System.out.println("This is a vowel");
				break;
			case 'i':
				System.out.println("This is a vowel");
				break;
			case 'o':
				System.out.println("This is a vowel");
				break;
			case 'u':
				System.out.println("This is a vowel");
				break;
			default:
				System.out.println("This is a consonant");
				break;
		}
	}

}
