package JavaPractice.JavaPractice;

public class IfElsePractice2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int grade1=80;
		int grade2=90; 
		
		if(grade1>grade2) {
            System.out.println("Grade 1 is greater than Grade 2");
		} else if (grade1 == grade2) {
			System.out.println("Grade 1 is equal to Grade 2");
		} else {
			System.out.println("Grade 2 is greater than Grade 1");
		}
		
		
	}

}
